  selector: 'app-carte-visite',
  templateUrl: './carte-visite.component.html',
  styleUrls: ['./carte-visite.component.css']
})
export class CarteVisiteComponent {
  user = {
    name: '',
    firstname: '',
    job: '',
    path: ''
  };
}
