import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-child',
  templateUrl: './child.component.html',
  styleUrls: ['./child.component.css']
})
export class ChildComponent {
  myFavColor: string = 'goldenrod'

  @Input() parentBgColor: string = '';
  @Output() changeColor = new EventEmitter<string>();

  changeParentBg() {
    this.changeColor.emit(this.myFavColor);
  }
}
<div>
    <h3>Child Component</h3>
    <p>My parent's background color is: <span [ngStyle]="{'background-color': parentBgColor}">{{parentBgColor}}</span></p>
    <p>My favourite color is: <span [ngStyle]="{'background-color': myFavColor}">{{ myFavColor}}</span></p>
    <button (click)="changeParentBg()">Pass the color to my parent</button>
</div>
