import { Component } from '@angular/core';

@Component({
  selector: 'app-color',
  templateUrl: './color.component.html',
  styleUrls: ['./color.component.css']
})
export class ColorComponent {

}
<div #colorDiv [style.backgroundColor]="currentColor" style="width: 200px; height: 200px; border: 1px solid black; margin-bottom: 10px;">
</div>

<input type="text" [(ngModel)]="inputColor" placeholder="Entrez une couleur" />
<button (click)="resetColor()">Réinitialiser</button>
